package numberOfCombinations

import (
	"testing"

	"github.com/stretchr/testify/assert"
)

func TestNumberOfCombinations(t *testing.T) {
	cases := []struct {
		wanted int
		given  string
	}{
		{2, "327"},
		{0, "094"},
		{0, "0"},
		{5, "9999"},
		{1, "10"},
		{1, "10000"},
		{755568658, ""},
	}

	for _, c := range cases {
		t.Run(c.given, func(t *testing.T) {
			assert.Equal(t, c.wanted, numberOfCombinations(c.given))
		})
	}
}

